int calc_floor(string para)
{
    int result = 0;
    for (size_t i = 0; i < para.length(); i++)
    {
        if (para[i] == '(')
        {
            result = result + 1;
        }
        else if (para[i] == ')')
        {
            result = result - 1;
        }
    }
    return result;
}

int base_pos(string para)
{
    int result = 0;
    size_t i = 0;
    for (; i < para.length(); i++)
    {
        if (para[i] == '(')
        {
            result = result + 1;
        }
        if (para[i] == ')')
        {
            result = result - 1;
        }
        if (result == -1)
        {
            return i+1;
        }
    }
}


